<template>
<header>
  <div class="container">
    <router-link to="/">
      <img src="data:image/webp;base64," class="logo">
    </router-link>
  </div>
</header>
<div class="container py-5">
  <h3>Lista de inscritos</h3>
  <div v-if="loading" class="loading">
    <img src="https://i.pinimg.com/originals/b4/4e/22/b44e229598a8bdb7f2f432f246fb0813.gif" style="width: auto; height: 150px; margin: 0 auto; display: table;">
  </div>
  <table class="table table-striped" v-if="!loading">
    <thead>
      <tr>
        <th scope="col">Nome</th>
        <th scope="col">E-mail</th>
      </tr>
    </thead>
    <tbody>
      <tr v-for="user in users" :key="user._id">
        <td>{{ user.name }}</td>
        <td>{{ user.email }}</td>
      </tr>
    </tbody>
  </table>
</div>
</template>

<script>
import axios from 'axios'
export default {
  data() {
    return {
      users: [],
      loading: true,
    }
  },
  async mounted() {
    try {
      await axios.get('https://subscribers-git-master-acioliwilson.vercel.app/subscribers')
      .then((response) => {
        this.users = response.data
        this.loading = false
      })

    } catch (error) {
      console.error(error)
      this.loading = false
    }
  }
}
</script>

<style>
header {
  width: 100%;
  display: flex;
  padding: 12px 0;
  background: linear-gradient(90deg, #2b6384 22.03%, #235273 85.57%);
}
.logo {
  width: auto;
  height: 70px;
}
</style>
